// hide effect

$("#hide").click(() => {
  $("#img").hide();
});

// show effect

$("#show").click(() => {
  $("#img").show();
});

// toggle effect

$("#toggle").click(() => {
  $("#img").toggle(2000);
});

// fades effect

$("#fadeIn").click(() => {
  $("#img").fadeIn(1000);
});

// show effect

$("#fadeOut").click(() => {
  $("#img").fadeOut(1000);
});

// toggle effect

$("#fadeToggle").click(() => {
  $("#img").fadeToggle(1000);
});

// slides effect

$("#slideUp").click(() => {
  $("#img").slideUp(1000);
});

// show effect

$("#slideDown").click(() => {
  $("#img").slideDown(1000);
});

// toggle effect

$("#slideToggle").click(() => {
  $("#img").slideToggle(1000);
});

$("#stop").click(() => {
  $("#img").stop();
});
